mapper.select:
select passwd 
from user
where id = #{id}
and mname = #{mname}

select id 
from user
where mname = #{mname}
and email = #{email}

select count(email)
from user
where email = #{email}

select count(id)
from user
where id = #{id}

select grade, mname
from user
where id = #{id}

select count(*) 
from user
where id = #{id}
and passwd = #{passwd}
