mapper.update:
update list
set recommend = recommend+1
where listno = #{listno}

UPDATE list
SET title=#{title}, content=#{content}
WHERE listno=#{listno}

update list
set cnt = cnt + 1
where listno = #{listno}

mapper.select:
SELECT listno, title, content,cnt,recommend, rdate
FROM list
WHERE listno=#{listno}
